import React from 'react'
import Select from './Select.tsx';
import Result from './Result.tsx';
import texts from "./texts.tsx";

const options: string[] = [];
texts.forEach((object) => !options.includes(object.answer) && options.push(object.answer));
console.log(options);

const TaskBox = (props: any) => {

    const handleChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
        const choice = event.target.value;
        const id = event.target.id;
        const thisObject: any = texts.find((object) => object?.id === id);
        // Correct answer -> Add id to correctAnswers state
        choice === thisObject.answer && console.log("correct");
        // Wrong answer -> Delete id from correctAnswers state
    };
    return <div className="task-box-container">
        <h2>{props.id}</h2>
        <p>{props.children}</p>
        <Select id={props.id} options={options} onChange={handleChange}/>
        <Result />
    </div>
}

export default TaskBox;
