import React from 'react'
import './App.css';
import { useState } from "react";
import TaskBox from './TaskBox.tsx';
import texts from "./texts.tsx";
import AllCorrect from './AllCorrect.tsx';
import getCorrectAnswers from './getCorrectAnswers.tsx';

const defaultAnswers = new Array(texts.length).fill(null);

function App() {
  const [answers, setAnswers] = useState(defaultAnswers);
  getCorrectAnswers(["Rabbit", "Mole", "Rabbit", "Rabbit"]);
  return (
    <section>
      <article>
        <i className="material-symbols-outlined large-icon">cognition</i>
        <h1>Quiz</h1>
        <main>
          {texts.map((object, index) => 
            <TaskBox 
              index={index} 
              answers={answers}
              setAnswers={answers}
              key={`taskbox ${index}`}
            >
              {object.text}
            </TaskBox>)
          }
          <AllCorrect correctAnswers={answers} />
        </main>
      </article>
    </section>

  );
}

export default App;
