import React from 'react'
import { useState } from "react";

const Result = (props: any) => {
    const [result, setResult] = useState(null);
    const isCorrect = props.correctAnswers.includes(props.id);
    return <div className={isCorrect ? "hidden" : "result-container correct"}>
        <i className="material-symbols-outlined">{result ? "check" : "toys_and_games"}</i>
        <p>Correct</p>
    </div>
}

export default Result;
